mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate = NOW()
WHERE id = #{id}

UPDATE article
SET hit = hit+1
WHERE id=#{id}

UPDATE article
SET likeCount = likeCount + 1
WHERE id = #{articleId};

UPDATE article
SET dislikeCount = dislikeCount + 1
WHERE id = #{articleId};

mapper.delete:
DELETE FROM reaction
WHERE memberId = #{memberId}
AND articleId = #{articleId}
