mapper.select:
SELECT reactionPoint
FROM reaction
WHERE memberId = #{memberId}
AND articleId = #{articleId}

SELECT COUNT(*)
FROM reaction
WHERE memberId = #{memberId}
AND articleId = #{articleId}

mapper.delete:
DELETE FROM reaction
WHERE memberId = #{memberId}
AND articleId = #{articleId}

mapper.update:
UPDATE article
SET likeCount = likeCount + 1
WHERE id = #{articleId}

UPDATE article
SET likeCount = likeCount - 1
WHERE id = #{articleId}
